import React, {useState} from 'react'
import './tags.css';


const Tags = () => {
    const [activeTag, setActiveTag] = useState(0);
    const handleTagClick = (index) => {setActiveTag(index);}

    return (
        <>
        <div className="tag-container">
        <button onClick={() => handleTagClick(1)} className="option-button">
            <div>Work</div>
        </button>
        <button onClick={() => handleTagClick(2)} className="option-button">
            <div>Short Break</div>
        </button>
        <button onClick={() => handleTagClick(3)} className="option-button">
            <div>Long Break</div>
        </button>
        </div>
        </>

    )
}

export default Tags
